//scrollpane parts
function sizeScrollbar(){var a=scrollContent.width()-scrollPane.width(),b=a/scrollContent.width(),c=scrollPane.width()-b*scrollPane.width();scrollbar.find(".ui-slider-handle").css({width:96,"margin-left":-48});handleHelper.width("").width(scrollbar.width()-96)}function resetValue(){var a=scrollPane.width()-scrollContent.width(),b=scrollContent.css("margin-left")==="auto"?0:parseInt(scrollContent.css("margin-left")),c=Math.round(b/a*100);scrollbar.slider("value",c)}function reflowContent(){var a=scrollContent.width()+parseInt(scrollContent.css("margin-left"),10),b=scrollPane.width()-a;b>0&&scrollContent.css("margin-left",parseInt(scrollContent.css("margin-left"),10)+b)}var scrollPane=$(".scroll-pane"),scrollContent=$(".scroll-content"),scrollbar=$(".scroll-bar").slider({slide:function(a,b){scrollContent.width()>scrollPane.width()?scrollContent.css("margin-left",Math.round(b.value/100*(scrollPane.width()-scrollContent.width()))+"px"):scrollContent.css("margin-left",0)}}),handleHelper=scrollbar.find(".ui-slider-handle").mousedown(function(){scrollbar.width(handleHelper.width())}).mouseup(function(){scrollbar.width("100%")}).append("<span class='ui-icon ui-icon-grip-dotted-vertical'></span>").wrap("<div class='ui-handle-helper-parent'></div>").parent();scrollPane.css("overflow","hidden");$(window).resize(function(){resetValue();sizeScrollbar();reflowContent()});setTimeout(sizeScrollbar,10);$(".lightbox-carousel").colorbox({rel:"lightbox-carousel",maxHeight:"95%",scalePhotos:!0,fixed:!0});$(".button.postings").ajaxDialog();